mapper.select:
select id, picture_path, url, show_order, is_show, create_time, update_time
from ad
where id = #{id,jdbcType=INTEGER}

mapper.update:
update ad
set url = #{url}
where id = #{adId}

update ad
set picture_path = #{picPath}
where id = #{adId}

update ad
set is_show = 1
where id = #{adId}

update ad
set is_show = 0
where id = #{adId}
