mapper.delete:
delete from cart_goods
where cart_id = #{cartId}
and is_chosen = 1

mapper.update:
update cart_goods
set amount = amount + #{amount,jdbcType=INTEGER}
where 1=1

update cart_goods

  set is_chosen = 0


  set is_chosen = 1

where cart_id = #{cartGoodsDto.cartId}
and is_goods = #{cartGoodsDto.isGoods}
and color_size_id = #{cartGoodsDto.colorSizeId}

mapper.select:
select id, cart_id, color_size_id, amount, is_goods, is_chosen, create_time, update_time
from cart_goods
where id = #{id,jdbcType=INTEGER}
