mapper.update:
update collection
set good_id = #{goodId,jdbcType=INTEGER},
  create_time = #{createTime,jdbcType=TIMESTAMP},
  update_time = #{updateTime,jdbcType=TIMESTAMP}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, good_id, create_time, update_time
from collection
where id = #{id,jdbcType=INTEGER}
