mapper.insert:
insert into color_size (id, goods_id, color, 
  size, create_time, update_time
  )
values (#{id,jdbcType=INTEGER}, #{goodsId,jdbcType=INTEGER}, #{color,jdbcType=VARCHAR}, 
  #{size,jdbcType=INTEGER}, #{createTime,jdbcType=TIMESTAMP}, #{updateTime,jdbcType=TIMESTAMP}
  )

mapper.select:
select id, goods_id, color, size, create_time, update_time
from color_size
where id = #{id,jdbcType=INTEGER}
