mapper.select:
select id, user_name, address_name, phone_number,is_default from user_address
where user_id = #{userId}
and is_default = 1

mapper.insert:
insert into user_address (id, user_id, address_name,
  phone_number, is_default
  )
values (#{id,jdbcType=INTEGER}, #{userId,jdbcType=INTEGER}, #{addressName,jdbcType=VARCHAR},
  #{phoneNumber,jdbcType=VARCHAR}, #{isDefault,jdbcType=BIT}
  )

mapper.update:
update user_address
set user_id = #{userId,jdbcType=INTEGER},
  address_name = #{addressName,jdbcType=VARCHAR},
  phone_number = #{phoneNumber,jdbcType=VARCHAR},
  is_default = #{isDefault,jdbcType=BIT}
where id = #{id,jdbcType=INTEGER}

